def longestPalindrome(s: str) -> str:
        subPalindrome = ''
        if (len(s) == 1): return s
        elif(len(s) == 2):
            if(s[0] == s[1]):
                return s
            else:
                return s[0]
        else:
            start = 0
            while start < len(s)-1:
                end = len(s)
                while start < end:
                    sub = s[start:end]
                    if (sub == sub[::-1]):
                        if len(sub) > len(subPalindrome):
                            subPalindrome = sub
                    end -= 1
                start += 1
            return subPalindrome

print(longestPalindrome(''))